// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.7.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
// import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract Exchange {
    using SafeERC20 for IERC20;
    mapping(address => uint256) public deposited;
    mapping(address => uint256) public toBeIssued;
    mapping(address => uint256) public issuedTokens;
    IERC20 public dexToken;
    IERC20 public dgtToken;
    address public admin;

    constructor(address _dexToken, address _dgtToken) {
        dexToken = IERC20(_dexToken);
        dgtToken = IERC20(_dgtToken);
        admin = msg.sender;
    }

    function depositCoins(uint256 _amount,uint256 _product) external {
        // dexToken.safeTransferFrom(msg.sender, address(this), _amount);
        dexToken.safeTransferFrom(msg.sender, address(this), _amount);

        deposited[msg.sender] += _amount;
        toBeIssued[msg.sender] += (_amount/_product)*(10**18);
    }
    // function issueTokens() external {
    //     dgtToken.safeTransfer(msg.sender, toBeIssued[msg.sender]);
    //     // dgtToken.safeTransfer(msg.sender, toBeIssued[msg.sender]);

    //     issuedTokens[msg.sender] += toBeIssued[msg.sender];
    //     toBeIssued[msg.sender] = 0;
    // }
     function issueTokens() external{
        dgtToken.safeTransfer(msg.sender, toBeIssued[msg.sender]);
        // dgtToken.safeTransfer(msg.sender, toBeIssued[msg.sender]);

        issuedTokens[msg.sender] += toBeIssued[msg.sender];
        toBeIssued[msg.sender] = 0;
    }
    function groupExecute(uint256 _amount,address _peer1,address _peer2) external{

        dexToken.safeTransferFrom(_peer1, _peer2, _amount);
        dgtToken.safeTransferFrom(_peer2, _peer1, _amount);
        // dexToken.safeTransferFrom(_peer1, address(this), _amount);
        // revert("1st Transaction");
        // dgtToken.safeTransferFrom(_peer2, address(this), _amount);

        // revert("1st Transaction");

        // revert("2nd Transaction");
        // dexToken.safeTransfer(_peer2, _amount);
        // revert("3rd Transaction");
        // dgtToken.safeTransfer(_peer1,_amount);
        // revert("4th Transaction");
    }
    function depositedCoins() external view returns (uint256) {
        return deposited[msg.sender];
    }

    function issuedToken() external view returns (uint256) {
        return issuedTokens[msg.sender];
    }
}
